import type { Metadata } from 'next'
import ProductsHeader from '@/components/products/ProductsHeader'
import ProductCategoryGrid from '@/components/products/ProductCategoryGrid'
import { getCategories, getFeaturedProducts } from '@/lib/data'
import ProductGrid from '@/components/products/ProductGrid'
import JsonLd, { generateFAQSchema, productsFAQs } from '@/components/seo/JsonLd'

export const metadata: Metadata = {
  title: 'Industrial Packaging Products | Yungsun Plastic Industry',
  description: 'Browse our comprehensive range of industrial packaging solutions including stretch films, strapping machines, carton sealers, conveyors, and plastic pallets. Best prices in Pakistan.',
  alternates: {
    canonical: 'https://yungsunplastic.com/products/',
  },
  openGraph: {
    title: 'Products - Yungsun Plastic Industry',
    description: 'Complete industrial packaging solutions for your business',
    images: ['/images/uploads/2023/11/H-banner-1-min.jpg'],
    url: 'https://yungsunplastic.com/products/',
  },
}

// Force static generation
export const dynamic = 'force-static'
export const revalidate = false

export default function ProductsPage() {
  const featuredProducts = getFeaturedProducts()
  const categories = getCategories()

  return (
    <div className="min-h-screen bg-white">
      <JsonLd data={generateFAQSchema(productsFAQs)} />
      <ProductsHeader />
      
      {/* Categories Section */}
      <section className="py-12">
        <div className="container-width">
          <h2 className="mb-8 text-2xl font-bold text-zinc-900">Browse by Category</h2>
          <ProductCategoryGrid />
        </div>
      </section>

      {/* Featured Products Section */}
      {featuredProducts.length > 0 && (
        <section className="border-t border-zinc-200 bg-zinc-50 py-12">
          <div className="container-width">
            <h2 className="mb-8 text-2xl font-bold text-zinc-900">Featured Products</h2>
            <ProductGrid products={featuredProducts} />
          </div>
        </section>
      )}
    </div>
  )
}

'use client'

import { useState } from 'react'
import { useForm } from 'react-hook-form'
import { zodResolver } from '@hookform/resolvers/zod'
import { Loader2, Mail } from 'lucide-react'
import Button from '@/components/ui/Button'
import { useCartStore } from '@/stores/cart'
import { contactFormSchema, type ContactFormData } from '@/lib/validations'

interface ContactFormProps {
  includeCartItems?: boolean
}

export default function ContactForm({ includeCartItems = false }: ContactFormProps) {
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [submitResult, setSubmitResult] = useState<{ success: boolean; message: string } | null>(null)
  
  const cartItems = useCartStore((state) => state.getCartItems())
  const clearCart = useCartStore((state) => state.clearCart)
  
  const {
    register,
    handleSubmit,
    reset,
    formState: { errors }
  } = useForm<ContactFormData>({
    resolver: zodResolver(contactFormSchema),
  })

  const onSubmit = async (data: ContactFormData) => {
    setIsSubmitting(true)
    setSubmitResult(null)

    try {
      // Build email body
      let emailBody = `Name: ${data.name}\n`
      emailBody += `Email: ${data.email}\n`
      if (data.phone) emailBody += `Phone: ${data.phone}\n`
      if (data.company) emailBody += `Company: ${data.company}\n`
      emailBody += `\nMessage:\n${data.message}`
      
      if (includeCartItems && cartItems.length > 0) {
        emailBody += `\n\n--- Quote Request Items ---\n`
        cartItems.forEach(item => {
          emailBody += `- ${item.product.name} (Qty: ${item.quantity})\n`
        })
      }

      // Open mailto link
      const subject = includeCartItems ? 'Quote Request - Yungsun Plastic Industry' : 'Contact Inquiry - Yungsun Plastic Industry'
      const mailtoUrl = `mailto:info@yungsunplastic.com?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(emailBody)}`
      
      window.open(mailtoUrl, '_blank')
      
      setSubmitResult({
        success: true,
        message: 'Your email client has been opened. Please send the email to complete your request.'
      })
      
      reset()
      if (includeCartItems) {
        clearCart()
      }
    } catch (error) {
      setSubmitResult({
        success: false,
        message: 'An error occurred. Please email us directly at info@yungsunplastic.com'
      })
    } finally {
      setIsSubmitting(false)
    }
  }

  return (
    <div className="rounded-lg border border-zinc-200 bg-white p-6">
      <h2 className="mb-4 text-2xl font-semibold text-zinc-900">
        {includeCartItems ? 'Request a Quote' : 'Send us a Message'}
      </h2>

      {submitResult && (
        <div className={`mb-4 rounded-md p-4 ${
          submitResult.success 
            ? 'bg-green-50 text-green-800 border border-green-200'
            : 'bg-red-50 text-red-800 border border-red-200'
        }`}>
          {submitResult.message}
        </div>
      )}

      {includeCartItems && cartItems.length > 0 && (
        <div className="mb-4 rounded-md bg-zinc-50 p-4">
          <h3 className="mb-2 font-medium text-zinc-900">Your Cart Items:</h3>
          <ul className="space-y-1 text-sm text-zinc-600">
            {cartItems.map(item => (
              <li key={item.product.id}>
                {item.product.name} x {item.quantity}
              </li>
            ))}
          </ul>
        </div>
      )}

      <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
        <div>
          <label className="mb-2 block text-sm font-medium text-zinc-900">
            Name *
          </label>
          <input
            {...register('name')}
            type="text"
            className={`w-full rounded-md border px-4 py-2 focus:outline-none focus:ring-2 focus:ring-zinc-900 ${
              errors.name ? 'border-red-500' : 'border-zinc-300'
            }`}
            disabled={isSubmitting}
          />
          {errors.name && (
            <p className="mt-1 text-sm text-red-600">{errors.name.message}</p>
          )}
        </div>

        <div>
          <label className="mb-2 block text-sm font-medium text-zinc-900">
            Email *
          </label>
          <input
            {...register('email')}
            type="email"
            className={`w-full rounded-md border px-4 py-2 focus:outline-none focus:ring-2 focus:ring-zinc-900 ${
              errors.email ? 'border-red-500' : 'border-zinc-300'
            }`}
            disabled={isSubmitting}
          />
          {errors.email && (
            <p className="mt-1 text-sm text-red-600">{errors.email.message}</p>
          )}
        </div>

        <div>
          <label className="mb-2 block text-sm font-medium text-zinc-900">
            Phone
          </label>
          <input
            {...register('phone')}
            type="tel"
            className="w-full rounded-md border border-zinc-300 px-4 py-2 focus:outline-none focus:ring-2 focus:ring-zinc-900"
            disabled={isSubmitting}
          />
        </div>

        <div>
          <label className="mb-2 block text-sm font-medium text-zinc-900">
            Company
          </label>
          <input
            {...register('company')}
            type="text"
            className="w-full rounded-md border border-zinc-300 px-4 py-2 focus:outline-none focus:ring-2 focus:ring-zinc-900"
            disabled={isSubmitting}
          />
        </div>

        <div>
          <label className="mb-2 block text-sm font-medium text-zinc-900">
            Message *
          </label>
          <textarea
            {...register('message')}
            rows={4}
            className={`w-full rounded-md border px-4 py-2 focus:outline-none focus:ring-2 focus:ring-zinc-900 ${
              errors.message ? 'border-red-500' : 'border-zinc-300'
            }`}
            disabled={isSubmitting}
          />
          {errors.message && (
            <p className="mt-1 text-sm text-red-600">{errors.message.message}</p>
          )}
        </div>

        <Button
          type="submit"
          disabled={isSubmitting || (includeCartItems && cartItems.length === 0)}
          className="w-full"
        >
          {isSubmitting ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              Submitting...
            </>
          ) : (
            includeCartItems ? 'Submit Quote Request' : 'Send Message'
          )}
        </Button>
      </form>
    </div>
  )
}

'use client'

import Link from 'next/link'
import Image from 'next/image'
import { useState, useRef, useEffect, useMemo, useCallback, memo } from 'react'
import { Menu, X, ChevronDown } from 'lucide-react'
import Button from '@/components/ui/Button'
import { useScrollDirection } from '@/hooks/useScrollDirection'
import { getCategories } from '@/lib/data'
import { useQuote } from '@/providers/QuoteProvider'

function Header() {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false)
  const [productsDropdownOpen, setProductsDropdownOpen] = useState(false)
  const dropdownTimeoutRef = useRef<NodeJS.Timeout | null>(null)
  const { scrollDirection, isAtTop } = useScrollDirection()
  const { openQuoteModal } = useQuote()
  
  // Memoize categories to prevent recalculation on every render
  const categories = useMemo(() => getCategories(), [])

  const handleMouseEnterDropdown = useCallback(() => {
    if (dropdownTimeoutRef.current) {
      clearTimeout(dropdownTimeoutRef.current)
    }
    setProductsDropdownOpen(true)
  }, [])

  const handleMouseLeaveDropdown = useCallback(() => {
    dropdownTimeoutRef.current = setTimeout(() => {
      setProductsDropdownOpen(false)
    }, 300)
  }, [])

  useEffect(() => {
    return () => {
      if (dropdownTimeoutRef.current) {
        clearTimeout(dropdownTimeoutRef.current)
      }
    }
  }, [])

  return (
    <header className="w-full bg-white shadow-sm">
        <div className="container mx-auto flex h-20 items-center justify-between px-4 sm:px-6 lg:px-8">
        {/* Logo */}
        <Link href="/" className="flex items-center">
          <Image
            src="/images/uploads/2023/11/yungsunplasticindustry-logo-2.png"
            alt="Yungsun Plastic Industry"
            width={180}
            height={60}
            className="h-12 w-auto"
            priority
          />
        </Link>

        {/* Desktop Navigation */}
        <nav className="hidden lg:flex lg:items-center lg:space-x-8">
          <Link
            href="/"
            className="text-sm font-medium text-zinc-700 transition-colors hover:text-zinc-900"
          >
            Home
          </Link>
          <Link
            href="/company-info"
            className="text-sm font-medium text-zinc-700 transition-colors hover:text-zinc-900"
          >
            Company Info
          </Link>
          
          {/* Products Mega Menu */}
          <div 
            className="relative"
            onMouseEnter={handleMouseEnterDropdown}
            onMouseLeave={handleMouseLeaveDropdown}
          >
            <button
              className="flex items-center gap-1 text-sm font-medium text-zinc-700 transition-colors hover:text-zinc-900"
              onClick={() => setProductsDropdownOpen(!productsDropdownOpen)}
            >
              Products
              <ChevronDown className={`h-4 w-4 transition-transform ${
                productsDropdownOpen ? 'rotate-180' : ''
              }`} />
            </button>
            
            {productsDropdownOpen && (
              <div className="absolute left-1/2 -translate-x-1/2 mt-2 w-[600px] rounded-lg bg-white shadow-xl ring-1 ring-black/5 p-4">
                <div className="grid grid-cols-2 gap-2">
                  {categories.map((category) => (
                    <Link
                      key={category.id}
                      href={`/products/${category.slug}`}
                      className="block px-4 py-3 text-sm text-zinc-700 hover:bg-yellow-50 hover:text-zinc-900 rounded-md transition-colors"
                      onClick={() => setProductsDropdownOpen(false)}
                    >
                      {category.name}
                    </Link>
                  ))}
                </div>
                <div className="border-t border-zinc-100 mt-3 pt-3">
                  <Link
                    href="/products"
                    className="block text-center text-sm font-medium text-blue-600 hover:text-blue-700"
                    onClick={() => setProductsDropdownOpen(false)}
                  >
                    View All Products →
                  </Link>
                </div>
              </div>
            )}
          </div>

          <Link
            href="/gallery"
            className="text-sm font-medium text-zinc-700 transition-colors hover:text-zinc-900"
          >
            Gallery
          </Link>
          <Link
            href="/contact"
            className="text-sm font-medium text-zinc-700 transition-colors hover:text-zinc-900"
          >
            Contact Us
          </Link>
        </nav>

        {/* Actions */}
        <div className="flex items-center space-x-4">
          {/* Request Quote Button - Desktop */}
          <Button
            variant="primary"
            size="default"
            className="hidden lg:flex"
            onClick={() => openQuoteModal()}
          >
            Request Quote
          </Button>

          {/* Mobile Menu Toggle */}
          <Button
            variant="ghost"
            size="sm"
            className="md:hidden"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            aria-label="Toggle menu"
          >
            {mobileMenuOpen ? (
              <X className="h-6 w-6" />
            ) : (
              <Menu className="h-6 w-6" />
            )}
          </Button>
        </div>
      </div>

      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <div className="border-t border-zinc-200 bg-white lg:hidden">
          <nav className="container mx-auto flex flex-col space-y-4 px-4 py-6">
            <Link
              href="/"
              className="text-base font-medium text-zinc-600 transition-colors hover:text-zinc-900"
              onClick={() => setMobileMenuOpen(false)}
            >
              Home
            </Link>
            <Link
              href="/company-info"
              className="text-base font-medium text-zinc-600 transition-colors hover:text-zinc-900"
              onClick={() => setMobileMenuOpen(false)}
            >
              Company Info
            </Link>
            <Link
              href="/products"
              className="text-base font-medium text-zinc-600 transition-colors hover:text-zinc-900"
              onClick={() => setMobileMenuOpen(false)}
            >
              Products
            </Link>
            <Link
              href="/gallery"
              className="text-base font-medium text-zinc-600 transition-colors hover:text-zinc-900"
              onClick={() => setMobileMenuOpen(false)}
            >
              Gallery
            </Link>
            <Link
              href="/contact"
              className="text-base font-medium text-zinc-600 transition-colors hover:text-zinc-900"
              onClick={() => setMobileMenuOpen(false)}
            >
              Contact Us
            </Link>
            <Button 
              variant="primary" 
              size="default" 
              className="w-full"
              onClick={() => {
                setMobileMenuOpen(false)
                openQuoteModal()
              }}
            >
              Request Quote
            </Button>
          </nav>
        </div>
      )}
    </header>
  )
}

// Export memoized component to prevent unnecessary re-renders
export default memo(Header)

'use client'

import { useState, useEffect, useRef } from 'react'
import Link from 'next/link'
import Image from 'next/image'
import { Search, X, Loader2 } from 'lucide-react'
import { searchProducts } from '@/lib/data'
import { useDebounce } from '@/hooks/useDebounce'
import type { Product } from '@/types'
import Button from '@/components/ui/Button'

interface SearchDialogProps {
  isOpen: boolean
  onClose: () => void
}

export default function SearchDialog({ isOpen, onClose }: SearchDialogProps) {
  const [query, setQuery] = useState('')
  const [results, setResults] = useState<Product[]>([])
  const [isSearching, setIsSearching] = useState(false)
  const debouncedQuery = useDebounce(query, 300)
  const inputRef = useRef<HTMLInputElement>(null)

  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus()
    }
  }, [isOpen])

  useEffect(() => {
    if (debouncedQuery.length >= 2) {
      setIsSearching(true)
      const searchResults = searchProducts(debouncedQuery)
      setResults(searchResults)
      setIsSearching(false)
    } else {
      setResults([])
    }
  }, [debouncedQuery])

  const handleClose = () => {
    setQuery('')
    setResults([])
    onClose()
  }

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      {/* Backdrop */}
      <div 
        className="fixed inset-0 bg-black/50 backdrop-blur-sm"
        onClick={handleClose}
      />

      {/* Dialog */}
      <div className="relative min-h-full flex items-start justify-center pt-[10vh]">
        <div className="relative w-full max-w-2xl mx-4 bg-white rounded-lg shadow-xl">
          {/* Search Input */}
          <div className="flex items-center border-b border-zinc-200">
            <Search className="ml-4 h-5 w-5 text-zinc-400" />
            <input
              ref={inputRef}
              type="text"
              placeholder="Search products..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              className="flex-1 px-4 py-4 text-zinc-900 placeholder-zinc-400 focus:outline-none"
            />
            {isSearching && (
              <Loader2 className="mr-4 h-5 w-5 text-zinc-400 animate-spin" />
            )}
            <button
              onClick={handleClose}
              className="mr-4 p-1 rounded-md hover:bg-zinc-100"
              aria-label="Close search"
            >
              <X className="h-5 w-5 text-zinc-500" />
            </button>
          </div>

          {/* Search Results */}
          <div className="max-h-[60vh] overflow-y-auto">
            {query.length >= 2 && (
              <>
                {results.length > 0 ? (
                  <div className="py-2">
                    {results.map((product) => (
                      <Link
                        key={product.id}
                        href={`/products/${product.category}/${product.slug}`}
                        onClick={handleClose}
                        className="flex items-center gap-4 px-4 py-3 hover:bg-zinc-50 transition-colors"
                      >
                        <div className="relative h-16 w-16 flex-shrink-0 overflow-hidden rounded-md bg-zinc-100">
                          <Image
                            src={product.images[0]}
                            alt={product.name}
                            fill
                            className="object-cover"
                            sizes="64px"
                          />
                        </div>
                        <div className="flex-1 min-w-0">
                          <h3 className="font-medium text-zinc-900 truncate">
                            {product.name}
                          </h3>
                          <p className="text-sm text-zinc-600 line-clamp-1">
                            {product.description}
                          </p>
                          <p className="text-xs text-zinc-400 mt-1">
                            in {product.categoryId}
                          </p>
                        </div>
                      </Link>
                    ))}
                  </div>
                ) : (
                  <div className="py-12 text-center">
                    <p className="text-zinc-500">
                      No products found for "{query}"
                    </p>
                  </div>
                )}
              </>
            )}

            {query.length === 1 && (
              <div className="py-12 text-center">
                <p className="text-zinc-400">
                  Type at least 2 characters to search
                </p>
              </div>
            )}

            {query.length === 0 && (
              <div className="py-12 text-center">
                <p className="text-zinc-400">
                  Start typing to search products
                </p>
              </div>
            )}
          </div>

          {/* Quick Links */}
          {query.length === 0 && (
            <div className="border-t border-zinc-200 px-4 py-3 bg-zinc-50">
              <p className="text-xs text-zinc-500 mb-2">QUICK LINKS</p>
              <div className="flex flex-wrap gap-2">
                <Link href="/products" onClick={handleClose}>
                  <Button variant="ghost" size="sm">
                    All Products
                  </Button>
                </Link>
                <Link href="/products/air-bag" onClick={handleClose}>
                  <Button variant="ghost" size="sm">
                    Air Bags
                  </Button>
                </Link>
                <Link href="/products/stretch-film" onClick={handleClose}>
                  <Button variant="ghost" size="sm">
                    Stretch Film
                  </Button>
                </Link>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { Users } from 'lucide-react'

export default function ViewingCounter() {
  const [viewCount, setViewCount] = useState(0)

  useEffect(() => {
    // Initial random count between 10-40
    setViewCount(Math.floor(Math.random() * 30) + 10)

    // Update count every 5-10 seconds
    const interval = setInterval(() => {
      setViewCount(prev => {
        const change = Math.floor(Math.random() * 7) - 3 // -3 to +3
        const newCount = prev + change
        // Keep within 10-40 range
        return Math.max(10, Math.min(40, newCount))
      })
    }, Math.random() * 5000 + 5000)

    return () => clearInterval(interval)
  }, [])

  return (
    <div className="flex items-center gap-2 text-sm text-green-600 bg-green-50 px-3 py-1.5 rounded-full">
      <Users className="h-4 w-4" />
      <span className="font-medium">{viewCount} users are viewing this product right now</span>
    </div>
  )
}

interface JsonLdProps {
  data: Record<string, unknown>
}

export default function JsonLd({ data }: JsonLdProps) {
  return (
    <script
      type="application/ld+json"
      dangerouslySetInnerHTML={{ 
        __html: JSON.stringify(data).replace(/</g, '\\u003c') 
      }}
    />
  )
}

